//не используется, заменил на вызов с Mocky.io
import express from 'express'
import bodyParser from 'body-parser'
import cors from 'cors'
import notebooks from './notebooks'

let nextId = 5;

const app = express();

// Using bodyParser middleware
app.use(bodyParser.json());

app.use((req, res, next) => {
    res.setHeader('Cache-Control', 'no-cache');
    next();
});

// Allow requests from any origin
app.use(cors({ origin: '*' }));

app.get('/api/notebooks', (req, res) => {
    res.send(notebooks);
});

app.post('/api/notebooks', (req, res) => {
    let notebook = {
        id: nextId++,
        name: req.body.name,
        price: req.body.price
    };

    notebooks.push(notebook);

    res.send(notebook);
});

app.put('/api/notebooks/:id', (req, res) => {
    let notebook = notebooks.find(notebook => notebook.id == req.params.id);

    if (!notebook) return res.sendStatus(404);

    notebook.title = req.body.title || notebook.title;

    res.json(notebook);
});

app.patch('/api/notebooks/:id', (req, res) => {
    let notebook = notebooks.find(notebook => notebook.id == req.params.id);

    if (!notebook) return res.sendStatus(404);

    notebook.completed = !notebook.completed;

    res.json(notebook);
});

app.delete('/api/notebooks/:id', (req, res) => {
    let index = notebooks.findIndex(notebook => notebook.id == req.params.id);
    
    if (index === -1) return res.sendStatus(404);

    notebooks.splice(index, 1);

    res.sendStatus(204);
});

app.listen(5000, 'localhost');